/// struct ContentView: View {
///     @State private var selectedTab: Tab = .dashboard
///     let container: DIContainer
///
///     var body: some View {
///         TabView(selection: $selectedTab) {
///             DashboardView(
///                 metabolismViewModel: container.makeMetabolismViewModel(),
///                 onNavigateToBody: {
