//
//  DashboardView.swift
//  Bodii
//
//  Created by Auto-Claude on 2026-01-12.
//

// 📚 학습 포인트: Dashboard View with Multiple Cards
// SwiftUI의 대시보드 화면 - 여러 모듈의 요약 정보를 한 곳에 표시
// 💡 Java 비교: Android의 Dashboard Fragment와 유사

import SwiftUI
import HealthKit

// MARK: - DashboardView

/// 대시보드 메인 화면
/// 📚 학습 포인트: Dashboard Pattern
/// - 여러 모듈의 요약 정보를 카드 형태로 표시
/// - 각 카드는 해당 모듈로 네비게이션 가능
/// - 주요 지표와 빠른 액세스 제공
/// 💡 Java 비교: Android의 Home Fragment + CardViews와 유사
struct DashboardView: View {

    // MARK: - Properties

    /// Metabolism ViewModel - BMR/TDEE 데이터 관리
    /// 📚 학습 포인트: @StateObject
    /// - View의 생명주기와 연결된 ObservableObject
    /// - Dashboard에서 대사율 데이터 관리
    /// 💡 Java 비교: Android ViewModel과 유사
    @StateObject private var metabolismViewModel: MetabolismViewModel

    /// HealthKit Settings ViewModel (Optional) - HealthKit 동기화 관리
    /// 📚 학습 포인트: Optional ViewModel for Feature Integration
    /// - HealthKit 연동이 활성화된 경우에만 동기화 수행
    /// - nil이면 HealthKit 기능 비활성화
    /// 💡 Java 비교: Optional<ViewModel> 패턴
    @StateObject private var healthKitViewModel: HealthKitSettingsViewModel?

    /// 체성분 탭으로 이동하는 콜백
    /// 📚 학습 포인트: Closure-based Navigation
    /// - 부모 View에서 탭 전환 로직을 처리
    /// - 카드 탭 시 호출되어 해당 탭으로 이동
    /// 💡 Java 비교: Callback interface와 유사
    var onNavigateToBody: (() -> Void)?

    /// Pull-to-refresh 상태
    /// 📚 학습 포인트: Refresh State
    @State private var isRefreshing = false

    // MARK: - Initialization

    /// DashboardView 초기화
    /// 📚 학습 포인트: Dependency Injection via Constructor
    /// - ViewModel과 네비게이션 콜백을 외부에서 주입받음
    /// - 테스트 시 Mock ViewModel 주입 가능
    /// 💡 Java 비교: Constructor injection과 유사
    ///
    /// - Parameters:
    ///   - metabolismViewModel: 대사율 ViewModel
    ///   - healthKitViewModel: HealthKit 설정 ViewModel (옵션)
    ///   - onNavigateToBody: 체성분 탭으로 이동하는 콜백
    init(
        metabolismViewModel: MetabolismViewModel,
        healthKitViewModel: HealthKitSettingsViewModel? = nil,
        onNavigateToBody: (() -> Void)? = nil
    ) {
        self._metabolismViewModel = StateObject(wrappedValue: metabolismViewModel)
        // 📚 학습 포인트: Optional StateObject Initialization
        // wrappedValue가 nil일 수 있는 경우 처리
        if let healthKitViewModel {
            self._healthKitViewModel = StateObject(wrappedValue: healthKitViewModel)
        } else {
            self._healthKitViewModel = StateObject(wrappedValue: nil)
        }
        self.onNavigateToBody = onNavigateToBody
    }

    // MARK: - Body

    var body: some View {
        NavigationStack {
            ScrollView {
                VStack(spacing: 20) {
                    // 환영 헤더
                    welcomeHeader

                    // HealthKit 동기화 상태 (활성화된 경우에만 표시)
                    if let healthKitViewModel = healthKitViewModel, healthKitViewModel.isEnabled {
                        healthKitSyncStatus
                    }

                    // 대사율 카드 (BMR/TDEE)
                    metabolismCard

                    // 추가 대시보드 카드들 (향후 구현)
                    // - 오늘의 식단 요약
                    // - 오늘의 운동 요약
                    // - 수면 요약
                    // - 주간 트렌드 차트

                    placeholderCards
                }
                .padding(.horizontal, 16)
                .padding(.vertical, 20)
            }
            .navigationTitle("대시보드")
            .navigationBarTitleDisplayMode(.large)
            .refreshable {
                await refreshData()
            }
            .task {
                // 📚 학습 포인트: task modifier
                // View가 나타날 때 비동기 작업 실행
                // 💡 Java 비교: onResume()에서 데이터 로드와 유사
                await metabolismViewModel.loadCurrentMetabolism()

                // HealthKit 상태 업데이트
                if let healthKitViewModel = healthKitViewModel {
                    await healthKitViewModel.refreshState()
                }
            }
            .alert("오류", isPresented: .constant(metabolismViewModel.errorMessage != nil)) {
                Button("확인") {
                    metabolismViewModel.clearError()
                }
            } message: {
                if let errorMessage = metabolismViewModel.errorMessage {
                    Text(errorMessage)
                }
            }
        }
    }

    // MARK: - Subviews

    /// 환영 헤더
    /// 📚 학습 포인트: Greeting Header
    /// - 현재 시간에 따라 인사말 변경 가능
    private var welcomeHeader: some View {
        VStack(alignment: .leading, spacing: 8) {
            HStack {
                VStack(alignment: .leading, spacing: 4) {
                    Text(greetingMessage)
                        .font(.title2)
                        .fontWeight(.bold)
                        .foregroundStyle(.primary)

                    Text("오늘의 건강 요약을 확인하세요")
                        .font(.subheadline)
                        .foregroundStyle(.secondary)
                }

                Spacer()

                // 날짜 표시
                VStack(alignment: .trailing, spacing: 2) {
                    Text(formatDate(Date()))
                        .font(.caption)
                        .fontWeight(.medium)
                        .foregroundStyle(.secondary)

                    Text(formatTime(Date()))
                        .font(.caption2)
                        .foregroundStyle(.secondary)
                }
            }
            .padding(.horizontal, 4)
        }
    }

    /// HealthKit 동기화 상태 표시
    /// 📚 학습 포인트: Sync Status Banner
    /// - 마지막 동기화 시각 표시
    /// - 동기화 진행 중일 때 ProgressView 표시
    /// - 수동 동기화 버튼 제공
    @ViewBuilder
    private var healthKitSyncStatus: some View {
        if let healthKitViewModel = healthKitViewModel {
            HStack(spacing: 12) {
                // Apple Health 아이콘
                Image(systemName: "heart.circle.fill")
                    .font(.title3)
                    .foregroundStyle(
                        LinearGradient(
                            colors: [.red, .pink],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        )
                    )

                VStack(alignment: .leading, spacing: 2) {
                    Text("Apple Health")
                        .font(.subheadline)
                        .fontWeight(.medium)
                        .foregroundStyle(.primary)

                    // 📚 학습 포인트: Last Sync Time Display
                    // RelativeDateTimeFormatter로 상대 시간 표시
                    if healthKitViewModel.isSyncing {
                        HStack(spacing: 4) {
                            ProgressView()
                                .scaleEffect(0.7)
                            Text("동기화 중...")
                                .font(.caption)
                                .foregroundStyle(.secondary)
                        }
                    } else if let lastSyncDate = healthKitViewModel.lastSyncDate {
                        Text("마지막 동기화: \(lastSyncDate, style: .relative)")
                            .font(.caption)
                            .foregroundStyle(.secondary)
                    } else {
                        Text("동기화 기록 없음")
                            .font(.caption)
                            .foregroundStyle(.secondary)
                    }
                }

                Spacer()

                // 📚 학습 포인트: Manual Sync Button
                // 수동 동기화 트리거
                if !healthKitViewModel.isSyncing {
                    Button(action: {
                        Task {
                            await healthKitViewModel.syncNow()
                        }
                    }) {
                        Image(systemName: "arrow.clockwise.circle.fill")
                            .font(.title2)
                            .foregroundStyle(.blue)
                    }
                    .disabled(!healthKitViewModel.canSync)
                }
            }
            .padding(12)
            .background(
                RoundedRectangle(cornerRadius: 12)
                    .fill(Color(.systemBackground))
                    .shadow(color: Color.black.opacity(0.05), radius: 4, x: 0, y: 2)
            )
            .overlay(
                RoundedRectangle(cornerRadius: 12)
                    .stroke(
                        LinearGradient(
                            colors: [.red.opacity(0.3), .pink.opacity(0.3)],
                            startPoint: .topLeading,
                            endPoint: .bottomTrailing
                        ),
                        lineWidth: 1
                    )
            )
        }
    }

    /// 대사율 카드
    /// 📚 학습 포인트: Reusable Card Component
    /// - MetabolismDisplayCard 컴포넌트 사용
    /// - 탭하면 체성분 탭으로 이동
    private var metabolismCard: some View {
        VStack(alignment: .leading, spacing: 12) {
            // 섹션 헤더
            sectionHeader(
                title: "대사율",
                subtitle: "기초대사량과 총 에너지 소비량"
            )

            // 대사율 표시 카드
            MetabolismDisplayCard(
                viewModel: metabolismViewModel,
                onTap: {
                    // 📚 학습 포인트: Callback Navigation
                    // 카드 탭 시 체성분 탭으로 이동 콜백 호출
                    onNavigateToBody?()
                }
            )
        }
    }

    /// 플레이스홀더 카드들
    /// 📚 학습 포인트: Future Implementation Placeholder
    /// - 향후 구현할 카드들의 위치 표시
    private var placeholderCards: some View {
        VStack(spacing: 20) {
            // 식단 카드 플레이스홀더
            placeholderCard(
                title: "오늘의 식단",
                subtitle: "칼로리 섭취 및 영양소 요약",
                icon: "fork.knife",
                color: .orange
            )

            // 운동 카드 플레이스홀더
            placeholderCard(
                title: "오늘의 운동",
                subtitle: "운동 시간 및 칼로리 소비",
                icon: "figure.run",
                color: .green
            )

            // 수면 카드 플레이스홀더
            placeholderCard(
                title: "수면 기록",
                subtitle: "수면 시간 및 품질",
                icon: "moon.zzz.fill",
                color: .purple
            )
        }
    }

    /// 섹션 헤더
    /// 📚 학습 포인트: Section Header Component
    /// - 카드 위에 표시되는 섹션 제목
    ///
    /// - Parameters:
    ///   - title: 섹션 제목
    ///   - subtitle: 섹션 부제목
    /// - Returns: 섹션 헤더 뷰
    private func sectionHeader(title: String, subtitle: String) -> some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.headline)
                .fontWeight(.semibold)
                .foregroundStyle(.primary)

            Text(subtitle)
                .font(.caption)
                .foregroundStyle(.secondary)
        }
        .padding(.horizontal, 4)
    }

    /// 플레이스홀더 카드
    /// 📚 학습 포인트: Placeholder Card Pattern
    /// - 향후 구현할 기능의 위치를 표시
    /// - 사용자에게 앱의 전체 구조를 미리 보여줌
    ///
    /// - Parameters:
    ///   - title: 카드 제목
    ///   - subtitle: 카드 부제목
    ///   - icon: SF Symbol 아이콘
    ///   - color: 아이콘 색상
    /// - Returns: 플레이스홀더 카드 뷰
    private func placeholderCard(
        title: String,
        subtitle: String,
        icon: String,
        color: Color
    ) -> some View {
        VStack(alignment: .leading, spacing: 16) {
            // 헤더
            HStack {
                Image(systemName: icon)
                    .font(.title2)
                    .foregroundStyle(color)

                Text(title)
                    .font(.headline)
                    .fontWeight(.semibold)
                    .foregroundStyle(.primary)

                Spacer()
            }

            // 콘텐츠
            HStack {
                Spacer()
                VStack(spacing: 8) {
                    Image(systemName: "hourglass")
                        .font(.largeTitle)
                        .foregroundStyle(.secondary)

                    Text("준비 중입니다")
                        .font(.subheadline)
                        .foregroundStyle(.secondary)

                    Text(subtitle)
                        .font(.caption)
                        .foregroundStyle(.secondary)
                        .multilineTextAlignment(.center)
                }
                .padding(.vertical, 20)
                Spacer()
            }
        }
        .padding(16)
        .background(cardBackground)
        .cornerRadius(12)
        .shadow(color: Color.black.opacity(0.05), radius: 8, x: 0, y: 2)
    }

    /// 카드 배경
    /// 📚 학습 포인트: Adaptive Colors
    /// - 라이트/다크 모드에 자동 대응하는 색상
    private var cardBackground: some View {
        RoundedRectangle(cornerRadius: 12)
            .fill(Color(.systemBackground))
            .overlay(
                RoundedRectangle(cornerRadius: 12)
                    .stroke(Color(.systemGray5), lineWidth: 1)
            )
    }

    // MARK: - Helper Methods

    /// 데이터 새로고침
    /// 📚 학습 포인트: Pull-to-Refresh with HealthKit Sync
    /// - 사용자가 아래로 당길 때 실행
    /// - HealthKit이 활성화된 경우 자동으로 동기화 수행
    /// 💡 Java 비교: SwipeRefreshLayout의 onRefresh와 유사
    private func refreshData() async {
        isRefreshing = true

        // 📚 학습 포인트: Parallel Async Tasks
        // 여러 ViewModel을 동시에 새로고침하여 성능 최적화
        await withTaskGroup(of: Void.self) { group in
            // Metabolism ViewModel 새로고침
            group.addTask {
                await metabolismViewModel.refresh()
            }

            // HealthKit 동기화 (활성화된 경우)
            if let healthKitViewModel = healthKitViewModel,
               healthKitViewModel.isEnabled,
               healthKitViewModel.canSync {
                group.addTask {
                    await healthKitViewModel.syncNow()
                }
            }

            // TODO: 다른 ViewModel들도 새로고침
        }

        isRefreshing = false
    }

    /// 인사말 메시지
    /// 📚 학습 포인트: Time-based Greeting
    /// - 현재 시간에 따라 다른 인사말 반환
    ///
    /// - Returns: 시간대별 인사말
    private var greetingMessage: String {
        let hour = Calendar.current.component(.hour, from: Date())

        switch hour {
        case 0..<6:
            return "좋은 밤입니다 👋"
        case 6..<12:
            return "좋은 아침입니다 ☀️"
        case 12..<18:
            return "좋은 오후입니다 🌤️"
        case 18..<21:
            return "좋은 저녁입니다 🌆"
        default:
            return "좋은 밤입니다 🌙"
        }
    }

    /// 날짜 포맷팅
    /// 📚 학습 포인트: Date Formatting
    ///
    /// - Parameter date: 날짜
    /// - Returns: 포맷된 문자열 (예: "2026년 1월 12일")
    private func formatDate(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.dateStyle = .long
        formatter.timeStyle = .none
        formatter.locale = Locale(identifier: "ko_KR")
        return formatter.string(from: date)
    }

    /// 시간 포맷팅
    /// 📚 학습 포인트: Time Formatting
    ///
    /// - Parameter date: 날짜
    /// - Returns: 포맷된 문자열 (예: "14:30")
    private func formatTime(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.timeStyle = .short
        formatter.dateStyle = .none
        formatter.locale = Locale(identifier: "ko_KR")
        return formatter.string(from: date)
    }
}

